CREATE PROCEDURE [dbo].[UpdateApplicaion]
(
	@AppID int,
	@AppName varchar(40),
	@AppVersion varchar(40),
	@AppDesc varchar(40)
)
AS

BEGIN SET NOCOUNT ON;
BEGIN TRY BEGIN TRANSACTION;
UPDATE Bugs SET AppID = @AppID ,UserID = @UserID ,BugSignOff = @BugSignOff ,BugDate = @BugDate ,BugDesc = @BugDesc ,BugDetails = @BugDetails ,RepSteps = @RepSteps ,FixDate = @FixDate WHERE BugID = @BugID;
INSERT INTO BugLog (BugLogDate ,StatusCodeID ,UserID ,BugLogDesc ,BugID) VALUES(GetDate(), @StatusCodeID, @UserID, @BugLogDesc, @BugID);
COMMIT TRANSACTION; END TRY BEGIN CATCH -- Rollback any active or uncommittable transactions before -- inserting information in the ErrorLog IF @@TRANCOUNT > 0 BEGIN ROLLBACK TRANSACTION; END END CATCH; END;

SET NOCOUNT ON
UPDATE Users set UserName = @UserName, UserEmail = @UserEmail, UserTel = @UserTel WHERE UserID = @UserID;
-- WARNING: Use @@rowcount immediately after the UPDATE statement if(@@ROWCOUNT = 0) BEGIN RAISERROR('Concurrency error detected', 16, 1) END
